    public EmailService()
    {
        From = "";
        To = "";
        Subject = "";
        Body = "";
    }
    public EmailService(string messageFrom, string messageTo, string subject, string body)
    {
        From = messageFrom;
        To = messageTo;
        Subject = subject;
        Body = body;
    }

    public string From { get; set; }
    public string To { get; set; }
    public string Subject { get; set; }
    public string Body { get; set; }

    public void Send() 
    {
        try
        {
            MailMessage message = new MailMessage();
            message.From = new MailAddress(From);
            message.To.Add(new MailAddress(To));
            message.Subject = Subject;
            message.Body = Body;
            message.IsBodyHtml = true;

            SmtpClient smtp = new SmtpClient("smtp.mandrillapp.com");
            smtp.Credentials = new System.Net.NetworkCredential("williamallenparks@gmail.com", "fL0vDO6dlO6JLNGJI_9-IA");
            smtp.Port = 25;
            smtp.EnableSsl = false;
            smtp.Send(message);
        }
        catch
