    public static void SendWelcomeEmail(string toEmail)
    {
        if (string.IsNullOrEmpty(toEmail))
            return;

        EmailService email = new EmailService("PowWow@JoinPowWow.com", toEmail, "Welcome to Pow Wow", welcomeEmail);
        email.Send();
    }

